import React, { useState, useEffect } from 'react';
import Desktop from './components/Win98UI/Desktop';
import LoginScreen from './components/LoginScreen';
import Window from './components/Win98UI/Window';
import Button from './components/Win98UI/Button';
import TrainingDemo from './components/TrainingDemo';
import CameraCapture from './components/CameraCapture';
import './components/Win98UI/styles.css';

// 添加全局CSS樣式
const globalStyles = `
  .win98-computer-content, 
  .win98-recyclebin-content,
  .win98-help-content {
    padding: 16px;
    font-family: 'MS Sans Serif', 'Tahoma', sans-serif;
    font-size: 12px;
  }

  .win98-help-section {
    margin-bottom: 16px;
  }

  .win98-help-section h4 {
    margin-top: 8px;
    margin-bottom: 8px;
    font-size: 12px;
  }

  .win98-help-actions {
    margin-top: 20px;
    display: flex;
    justify-content: center;
  }

  .win98-empty-container {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    gap: 16px;
    height: 100%;
  }
`;

const App = () => {
    // 狀態管理
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [username, setUsername] = useState('');
    const [activeWindows, setActiveWindows] = useState([]);
    const [activeWindowId, setActiveWindowId] = useState(null);

    // 初始化時添加樣式 - 移到這裡
    useEffect(() => {
      const style = document.createElement('style');
      style.textContent = globalStyles;
      document.head.appendChild(style);

      return () => {
        document.head.removeChild(style);
      };
    }, []);

    // 檢查是否已經登入
    useEffect(() => {
      const savedUsername = localStorage.getItem('win98_username');
      if (savedUsername) {
        setUsername(savedUsername);
        setIsLoggedIn(true);
      }
    }, []);

  // 處理登入
  const handleLogin = (user) => {
    setUsername(user);
    setIsLoggedIn(true);
  };

  // 處理登出
  const handleLogout = () => {
    localStorage.removeItem('win98_username');
    setUsername('');
    setIsLoggedIn(false);
    setActiveWindows([]);
  };

  // 處理圖標點擊
  const handleIconClick = (icon) => {
    switch (icon.id) {
      case 'vision-trainer':
        openWindow('trainer');
        break;
      case 'my-computer':
        openWindow('my-computer');
        break;
      case 'recyclebin':
        openWindow('recyclebin');
        break;
      case 'help':
        openWindow('help');
        break;
      case 'logout':
        handleLogout();
        break;
      default:
        break;
    }
  };

  // 打開窗口
  const openWindow = (id) => {
    if (!activeWindows.includes(id)) {
      setActiveWindows([...activeWindows, id]);
    }
    setActiveWindowId(id);
  };

  // 關閉窗口
  const closeWindow = (id) => {
    setActiveWindows(activeWindows.filter(windowId => windowId !== id));
    if (activeWindowId === id) {
      setActiveWindowId(activeWindows.length > 1 ? activeWindows[activeWindows.length - 2] : null);
    }
  };

  // 桌面圖標配置
  const desktopIcons = [
    {
      id: 'vision-trainer',
      icon: 'data:image/png;base64,',
      label: '機器視覺訓練'
    },
    {
      id: 'my-computer',
      icon: 'my-computer',
      label: '我的電腦'
    },
    {
      id: 'recyclebin',
      icon: 'recycle-bin',
      label: '資源回收筒'
    },
    {
      id: 'help',
      icon: 'data:image/png;base64,',
      label: '說明'
    },
    {
      id: 'logout',
      icon: 'data:image/png;base64,',
      label: '登出'
    }
  ];

  // 窗口配置
const windows = [
    {
        id: 'trainer',
        title: '機器視覺訓練',
        // 使用 desktopIcons 中定義的同一圖標
        icon: 'data:image/png;base64,',
        content: <TrainingDemo onClose={() => closeWindow('trainer')} />,
        position: { x: 100, y: 100 },
        size: { width: 700, height: 500 },
        resizable: true,
        minimizable: true,
        maximizable: true
      },
    {
      id: 'my-computer',
      title: '我的電腦',
      icon: 'my-computer',
      content: (
        <div className="win98-computer-content">
          <h3>我的電腦</h3>
          <p>這是一個簡化的Windows 98界面模擬，用於展示機器視覺訓練系統。</p>
          <p>請點擊桌面上的「機器視覺訓練」圖標來開始體驗。</p>
        </div>
      ),
      position: { x: 150, y: 120 },
      size: { width: 400, height: 300 },
      resizable: true,
      minimizable: true,
      maximizable: true
    },
    {
      id: 'recyclebin',
      title: '資源回收筒',
      icon: 'recycle-bin',
      content: (
        <div className="win98-recyclebin-content">
          <div className="win98-empty-container">
            <p>資源回收筒是空的</p>
            <Button onClick={() => closeWindow('recyclebin')}>關閉</Button>
          </div>
        </div>
      ),
      position: { x: 200, y: 150 },
      size: { width: 350, height: 250 },
      resizable: true,
      minimizable: true,
      maximizable: true
    },
    {
      id: 'help',
      title: '說明',
      icon: 'data:image/png;base64,...',
      content: (
        <div className="win98-help-content">
          <h3>Windows 98 風格機器視覺演示系統</h3>

          <div className="win98-help-section">
            <h4>使用說明：</h4>
            <p>本系統模擬了Windows 98的經典界面，並提供了一個簡化的機器視覺訓練流程展示。</p>
          </div>

          <div className="win98-help-section">
            <h4>功能介紹：</h4>
            <ul>
              <li>雙擊「機器視覺訓練」圖標開始體驗</li>
              <li>使用相機拍攝樣本圖像</li>
              <li>觀看資料增強效果</li>
              <li>模擬訓練過程</li>
            </ul>
          </div>

          <div className="win98-help-section">
            <h4>操作技巧：</h4>
            <ul>
              <li>窗口可以拖動、調整大小</li>
              <li>任務欄顯示打開的窗口</li>
              <li>點擊開始菜單可以查看系統選項</li>
            </ul>
          </div>

          <div className="win98-help-actions">
            <Button onClick={() => closeWindow('help')}>關閉說明</Button>
          </div>
        </div>
      ),
      position: { x: 250, y: 180 },
      size: { width: 450, height: 350 },
      resizable: true,
      minimizable: true,
      maximizable: true
    }
  ];

  return (
    <React.Fragment>
      {!isLoggedIn ? (
        <LoginScreen onLogin={handleLogin} />
      ) : (
        <Desktop
          username={username}
          icons={desktopIcons}
          windows={windows}
          onIconClick={handleIconClick}
          activeWindows={activeWindows}
          activeWindowId={activeWindowId}
        />
      )}
    </React.Fragment>
  ); // ✅ 這行是報錯的根源，現在已正確對應
};

export default App;
